SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_itens_consist_manual ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, qt_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_unidade_medida_compra_w	varchar(30);
cd_material_w	integer;
qt_existe_w			bigint;


BEGIN
if (coalesce(qt_atendimento_p,0) > 0) then
	begin
	select	cd_unidade_medida_compra,
		cd_material
	into STRICT	cd_unidade_medida_compra_w,
		cd_material_w
	from	ordem_compra_item
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci	= nr_item_oci_p;

	select	count(*)
	into STRICT	qt_existe_w
	from	ordem_compra_item_cb
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci = nr_item_oci_p
	and	cd_unidade_medida_compra = cd_unidade_medida_compra_w
	and	coalesce(nr_seq_nota::text, '') = ''
	and	ie_atende_recebe = 'A'
	and	coalesce(nr_seq_lote::text, '') = ''
	and	ie_status = 'CM';

	if (qt_existe_w > 0) then
		update ordem_compra_item_cb
		set	qt_material = qt_atendimento_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_ordem_compra = nr_ordem_compra_p
		and	nr_item_oci = nr_item_oci_p
		and	cd_unidade_medida_compra = cd_unidade_medida_compra_w
		and	coalesce(nr_seq_nota::text, '') = ''
		and	ie_atende_recebe = 'A'
		and	coalesce(nr_seq_lote::text, '') = ''
		and	ie_status = 'CM';
	else
		insert into ordem_compra_item_cb(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_ordem_compra,
			nr_item_oci,
			cd_material,
			cd_unidade_medida_compra,
			qt_material,
			nr_seq_lote,
			ie_atende_recebe,
			ie_status)
		values (	nextval('ordem_compra_item_cb_seq'),
			clock_timestamp(),
			nm_usuario_p,
			nr_ordem_compra_p,
			nr_item_oci_p,
			cd_material_w,
			cd_unidade_medida_compra_w,
			qt_atendimento_p,
			'',
			'A',
			'CM');
	end if;
	end;
else
	delete	FROM ordem_compra_item_cb
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci = nr_item_oci_p
	and	coalesce(nr_seq_nota::text, '') = ''
	and	ie_atende_recebe = 'A'
	and	coalesce(nr_seq_lote::text, '') = ''
	and	ie_status = 'CM';
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
