CREATE OR REPLACE PROCEDURE gravar_just_exame_ext (nr_seq_exame_p bigint, ds_justificativa_p text, ds_alerta_p text, nm_usuario_p text) AS $body$
BEGIN
if (coalesce(nr_seq_exame_p,0) > 0) then

	insert into pedido_exame_externo_just(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_sol_exame,
						ds_justificativa,
						ds_alerta)
					values (	nextval('pedido_exame_externo_just_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_exame_p,
						ds_justificativa_p,
						ds_alerta_p);
	commit;

end if;

end;
