CREATE OR REPLACE PROCEDURE gravar_log_atend_orcamento (nr_atendimento_p bigint, nr_seq_orcamento_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


dt_orcamento_w		timestamp;
cd_convenio_w		integer;
vl_total_w		double precision;
ie_tipo_atend_orc_w	smallint;
nm_usuario_orc_w	varchar(15);


BEGIN

select	a.dt_orcamento,
        a.cd_convenio,
        obter_valor_orc_pac(a.nr_sequencia_orcamento) vl_total_orc,
	ie_tipo_atendimento,
	coalesce(nm_usuario_nrec, nm_usuario)
into STRICT	dt_orcamento_w,
	cd_convenio_w,
	vl_total_w,
	ie_tipo_atend_orc_w,
	nm_usuario_orc_w
from    orcamento_paciente a
where   a.nr_sequencia_orcamento = nr_seq_orcamento_p;

if (ie_acao_p = 'V') then
	update 	Orcamento_paciente
	set    	nr_atendimento = nr_atendimento_p
	where  	nr_sequencia_orcamento = nr_seq_orcamento_p;
elsif (ie_acao_p = 'D') then
	update 	Orcamento_paciente
	set    	nr_atendimento  = NULL
	where  	nr_sequencia_orcamento = nr_seq_orcamento_p;
end if;

insert into atendimento_orcamento_hist(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_atendimento,
		nr_sequencia_orcamento,
		cd_convenio_orc,
		ie_tipo_atend_orc,
		ie_acao,
		vl_orcamento,
		dt_orcamento,
		nm_usuario_orc)
	values (nextval('atendimento_orcamento_hist_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_atendimento_p,
		nr_seq_orcamento_p,
		cd_convenio_w,
		ie_tipo_atend_orc_w,
		coalesce(ie_acao_p,'V'),
		vl_total_w,
		dt_orcamento_w,
		nm_usuario_orc_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
