CREATE OR REPLACE PROCEDURE gravar_log_atualizacao_erro_atx ( nr_seq_log_p bigint, ds_comando_p text, ds_erro_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_log_w bigint;
ds_module_w	varchar(48);
BEGIN
	begin
	nr_seq_log_w := nr_seq_log_p;
	if (coalesce(nr_seq_log_w::text, '') = '') then
		ds_module_w := wheb_usuario_pck.get_ds_form;
		nr_seq_log_w := substr(ds_module_w,18,10);
	end if;

	insert into Log_Atualizacao_Erro(
		nr_sequencia,
		ds_erro,
		ds_comando,
		nr_seq_log,
		dt_atualizacao,
		nm_usuario)
	values (
		nextval('log_atualizacao_erro_seq'),
		substr(ds_erro_p,1,2000),
		substr(ds_comando_p,1,2000),
		nr_seq_log_w,
		clock_timestamp(),
		substr(nm_usuario_p,1,15));
	commit;
	exception
	when others then
		ds_module_w := ds_module_w;
	end;
	commit;
end	;
$body$
