CREATE OR REPLACE PROCEDURE gravar_log_calculo_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_propaci_p bigint, cd_convenio_p bigint, cd_categoria_p text, ds_log_calculo_p text, ie_gravar_log_p text) AS $body$
DECLARE


ie_existe_w		varchar(1):= 'S';
ie_origem_proced_w	bigint;		
qt_reg_w		integer;
					

BEGIN

if (coalesce(ie_gravar_log_p,'N') = 'S') and (obter_funcao_ativa in (67,1115,99071,99070)) then
	
	select 	count(*)
	into STRICT	qt_reg_w
	from 	procedimento
	where 	cd_procedimento = cd_procedimento_p
	and 	coalesce(ie_origem_proced_p::text, '') = '';
	
	if (coalesce(ie_origem_proced_p::text, '') = '') then

		if (qt_reg_w > 0) then
		
			select 	max(ie_origem_proced)
			into STRICT	ie_origem_proced_w
			from 	procedimento
			where 	cd_procedimento = cd_procedimento_p
			and 	coalesce(ie_origem_proced_p::text, '') = '';
		
			ie_existe_w:= 'S';
			
		else
		
			ie_existe_w:= 'N';
			
		end if;
	else
	
		select	coalesce(max('S'),'N')
		into STRICT	ie_existe_w
		from	procedimento
		where	cd_procedimento = cd_procedimento_p
		and	ie_origem_proced = ie_origem_proced_p;
		
	end if;	
		
	if (ie_existe_w = 'S') then
		
		insert  into procedimento_log_calculo( nr_sequencia,
							cd_procedimento,
							ie_origem_proced,
							dt_atualizacao,
							nr_seq_propaci,
							cd_convenio,
							cd_categoria,
							ds_log_calculo  )
					values (     nextval('procedimento_log_calculo_seq'),
							cd_procedimento_p,
							coalesce(ie_origem_proced_p,ie_origem_proced_w),
							clock_timestamp(),
							nr_seq_propaci_p,
							cd_convenio_p,
							cd_categoria_p,
							ds_log_calculo_p);
							
	end if;

        if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
