CREATE OR REPLACE PROCEDURE gravar_log_contrato ( nr_seq_contrato_p bigint, nm_usuario_p text, nm_atributo_p text, vl_antigo_p text, vl_novo_p text, ie_novo_log text) AS $body$
DECLARE


nr_seq_log_contrato_w	bigint;
nr_seq_log_contr_item_w	bigint;
max_contrato_w			bigint;
nr_seq_item_w			bigint;


BEGIN
if (ie_novo_log = 'S') then
	begin
	select	max(nr_sequencia)
	into STRICT	max_contrato_w
	from	log_alteracao_contr;

	if (max_contrato_w IS NOT NULL AND max_contrato_w::text <> '') then
		nr_seq_log_contrato_w := max_contrato_w + 1;
	else
		nr_seq_log_contrato_w := 1;
	end if;

	insert	into	log_alteracao_contr(nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_contrato)
			values (nr_seq_log_contrato_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_contrato_p);
	end;
else
	begin
	select	max(nr_sequencia)
	into STRICT	nr_seq_log_contrato_w
	from	log_alteracao_contr;
	end;
end if;

select	max(nr_sequencia)
into STRICT	nr_seq_log_contr_item_w
from	log_alteracao_contr;

select	max(nr_sequencia)
into STRICT	nr_seq_item_w
from	log_altr_contr_item;

if (nr_seq_item_w IS NOT NULL AND nr_seq_item_w::text <> '') then
	nr_seq_item_w := nr_seq_item_w + 1;
else
	nr_seq_item_w := 1;
end if;

insert	into	log_altr_contr_item(nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ds_campo,
				vl_antigo,
				vl_novo,
				nr_seq_log_contr)
		values (nr_seq_item_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nm_atributo_p,
				vl_antigo_p,
				vl_novo_p,
				nr_seq_log_contr_item_w);

commit;

end;
