CREATE OR REPLACE PROCEDURE gravar_log_cpoe (ds_log_w text, nr_atendimento_p bigint default null, ie_tipo_item_p text default null, nr_seq_cpoe_p bigint default null, ds_processo_p text default null) AS $body$
DECLARE


  ds_backtrace_w varchar(4000);


BEGIN

	begin
		ds_backtrace_w := substr('Backtrace: ' || chr(13) || dbms_utility.format_error_backtrace || chr(13), 1, 4000);
	exception when others then
		ds_backtrace_w := '';
	end;

	insert into log_cpoe(
		nr_sequencia,
		dt_atualizacao, 
		nm_usuario, 
		ds_log, 
		nr_seq_material, 
		nr_seq_dieta, 
		nr_seq_gasoterapia, 
		nr_seq_recomendacao, 
		nr_seq_procedimento, 
		nr_seq_dialise, 
		nr_seq_hemoterapia, 
		ds_stack, 
		nr_atendimento,
        ds_processo)
	values (nextval('log_cpoe_seq'),
		clock_timestamp(), 
		coalesce(wheb_usuario_pck.get_nm_usuario, 'TASY'), 
		substr(ds_log_w,1,2000), 
		CASE WHEN ie_tipo_item_p='M' THEN  nr_seq_cpoe_p  ELSE null END ,
		CASE WHEN ie_tipo_item_p='N' THEN  nr_seq_cpoe_p  ELSE null END ,
		CASE WHEN ie_tipo_item_p='G' THEN  nr_seq_cpoe_p  ELSE null END ,
		CASE WHEN ie_tipo_item_p='R' THEN  nr_seq_cpoe_p  ELSE null END ,
		CASE WHEN ie_tipo_item_p='P' THEN  nr_seq_cpoe_p  ELSE null END ,
		CASE WHEN ie_tipo_item_p='D' THEN  nr_seq_cpoe_p  ELSE null END ,
		CASE WHEN ie_tipo_item_p='H' THEN  nr_seq_cpoe_p  ELSE null END ,
		substr(ds_backtrace_w || dbms_utility.format_call_stack, 1, 4000),
		nr_atendimento_p,
        ds_processo_p);

exception when others then
	null;
end;
$body$
