CREATE OR REPLACE PROCEDURE gravar_log_impressao_arq_cobr (nr_seq_lote_p bigint, nr_seq_imp_p bigint, nr_seq_titulo_p bigint, nr_seq_conta_p bigint, ds_arquivo_p text, nm_usuario_p text) AS $body$
DECLARE


qt_reg		bigint;


BEGIN

select	count(*)
into STRICT	qt_reg
from	cobranca_paciente_imp_arq
where	nr_seq_impressao = nr_seq_imp_p
and	coalesce(nr_seq_cob_titulo,0)	= coalesce(nr_seq_titulo_p,0)
and	coalesce(nr_seq_cob_conta,0)		= coalesce(nr_seq_conta_p,0)
and	coalesce(ds_arquivo,'X')		= coalesce(ds_arquivo_p,'X');

if (coalesce(nr_seq_lote_p,0) > 0) and (coalesce(qt_reg,0) = 0) then

	insert into cobranca_paciente_imp_arq(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_impressao,
		nr_seq_cob_titulo,
		nr_seq_cob_conta,
		ds_arquivo)
	values (	nextval('cobranca_paciente_imp_arq_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_imp_p,
		nr_seq_titulo_p,
		nr_seq_conta_p,
		substr(ds_arquivo_p,1,255));
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
