CREATE OR REPLACE PROCEDURE gravar_log_integracao_laudo ( nr_acesso_dicom_p text, ds_log_p text, nm_usuario_p text ) AS $body$
DECLARE


ie_gravar_log_w		varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_gravar_log_w
from	parametro_integracao_pacs
where	ie_gravar_log_integracao = 'S';


if (ie_gravar_log_w = 'S') then

	insert into log_integracao_laudo(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_acesso_dicom,
		ds_log)
	values (	nextval('log_integracao_laudo_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nr_acesso_dicom_p,
		ds_log_p);

end if;


end;
