CREATE OR REPLACE PROCEDURE gravar_log_motivo_pausa_senha ( nr_seq_motivo_p bigint, ds_observacao_p text, nr_seq_status_p bigint default 0) AS $body$
DECLARE


	ie_situacao_w	motivo_pausa_senha.ie_situacao%type;
	nr_seq_w	log_motivo_pausa_senha.nr_sequencia%type;
	nm_usuario_w	usuario.nm_usuario%type;

BEGIN

if (coalesce(nr_seq_motivo_p,0) > 0) then
	
	select	nextval('log_motivo_pausa_senha_seq')
	into STRICT	nr_seq_w
	;
	
	nm_usuario_w := obter_usuario_ativo;
	
	select 	coalesce(ie_situacao, 'A')
	into STRICT 	ie_situacao_w
	from 	motivo_pausa_senha
	where 	nr_sequencia = nr_seq_motivo_p;

	insert into log_motivo_pausa_senha(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_motivo,
		ds_observacao,
		ie_situacao)
	values (nr_seq_w,
		clock_timestamp(),
		nm_usuario_w,
		clock_timestamp(),
		nm_usuario_w,
		nr_seq_motivo_p,
		coalesce(ds_observacao_p, ''),
		ie_situacao_w);

	commit;
	
	if (coalesce(nr_seq_status_p,0) > 0) then
		CALL alterar_usuario_status_senha(nm_usuario_w, nm_usuario_w, nr_seq_status_p);
	end if;
		
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
