SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_log_pabx_ger_tel ( dt_log_p text, ieAmPm_p text, nr_ramal_p text, nr_acao_p bigint, ds_log_p text, nm_usuario_p text) AS $body$
DECLARE

 
dt_log_w		timestamp;
hr_log_w		integer;
ds_acao_w		varchar(255);
cd_setor_atendimento_w	bigint;
cd_unidade_basica_w	varchar(10);
cd_unidade_compl_w	varchar(10);
nr_seq_interno_w	bigint;

BEGIN
 
 
begin 
	if ('PM' = coalesce(upper(dt_log_p),'AM')) then 
		hr_log_w	:= (obter_somente_numero(substr(dt_log_p,10,2)))::numeric +12;
		dt_log_w	:= to_date(substr(dt_log_p,1,9) || hr_log_w || substr(dt_log_p,12,16), 'dd/MM/yyyy hh24:mi:ss');
	else 
		dt_log_w	:= to_date(dt_log_p || ':00', 'dd/MM/yyyy hh24:mi:ss');
	end if;
 
exception 
	when others then 
	dt_log_w := clock_timestamp();
end;
 
			  
if (nr_acao_p > 0) then 
	 
	select	CASE WHEN 	nr_acao_p=1 THEN wheb_mensagem_pck.get_texto(802480) WHEN 	nr_acao_p=2 THEN wheb_mensagem_pck.get_texto(802481) WHEN 	nr_acao_p=3 THEN wheb_mensagem_pck.get_texto(802482) WHEN 	nr_acao_p=4 THEN wheb_mensagem_pck.get_texto(802483)||'('||wheb_mensagem_pck.get_texto(802485)||')' WHEN 	nr_acao_p=5 THEN wheb_mensagem_pck.get_texto(802483)||'('||wheb_mensagem_pck.get_texto(802486)||' '||wheb_mensagem_pck.get_texto(802483)||')'  ELSE '' END  ds_acao 
	into STRICT	ds_acao_w 
	;	
	 
	select 	max(nr_seq_interno) 
	into STRICT	nr_seq_interno_w 
	from  ocupacao_unidade_v o 
	where  o.nr_ramal = nr_ramal_p;
	 
	select	max(cd_setor_atendimento), 
		max(cd_unidade_basica), 
		max(cd_unidade_compl) 
	into STRICT	cd_setor_atendimento_w, 
		cd_unidade_basica_w, 
		cd_unidade_compl_w 
	from  ocupacao_unidade_v o 
	where  o.nr_ramal = nr_ramal_p 
	and	o.nr_seq_interno = nr_seq_interno_w;
 
 
	insert into	pabx_log(nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		ds_log, 
		dt_log, 
		nr_ramal, 
		ds_acao, 
		cd_setor_atendimento, 
		cd_unidade_basica, 
		cd_unidade_compl) 
	values (nextval('pabx_log_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		ds_log_p, 
		dt_log_w, 
		nr_ramal_p, 
		ds_acao_w, 
		cd_setor_atendimento_w, 
		cd_unidade_basica_w, 
		cd_unidade_compl_w);
 
	 
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
