CREATE OR REPLACE PROCEDURE gravar_log_processo ( cd_log_p bigint, dt_Inicio_P timestamp, NM_USUARIO_P text, ds_processo_P text) AS $body$
DECLARE

ds_tempo_w			varchar(10);
qt_dia_w			double precision;
qt_seg_w			bigint;


BEGIN
qt_dia_w			:= (clock_timestamp() - dt_inicio_p);
qt_seg_w			:= qt_dia_w * 24 * 60 * 60;

ds_tempo_w 			:= to_char(qt_seg_w);

