CREATE OR REPLACE PROCEDURE gravar_log_prot_convenio ( nr_seq_protocolo_p bigint, nm_usuario_p text, ie_tipo_log_p text) AS $body$
DECLARE

 
vl_protocolo_w		double precision;
ds_arquivo_envio_w	varchar(255);
ds_observacao_w		varchar(255);
ie_status_protocolo_w	smallint;
qt_existe_w		varchar(1);


BEGIN 
 
if (ie_tipo_log_p = 'G') then 
 
	select	coalesce(max('S'),'N') 
	into STRICT	qt_existe_w 
	from	protocolo_convenio_log 
	where	nr_seq_protocolo = nr_seq_protocolo_p;
	 
	if (qt_existe_w = 'S') then 
		goto final;
	end if;
	 
end if;
 
select 	ds_arquivo_envio, 
	coalesce(obter_total_protocolo(nr_seq_protocolo),0), 
	ie_status_protocolo 
into STRICT	ds_arquivo_envio_w, 
	vl_protocolo_w, 
	ie_status_protocolo_w 
from 	protocolo_convenio 
where 	nr_seq_protocolo = nr_seq_protocolo_p;
 
if (ie_tipo_log_p = 'T') then 
	ds_observacao_w:= WHEB_MENSAGEM_PCK.get_texto(278977);
elsif (ie_tipo_log_p = 'M') then 
	ds_observacao_w:= WHEB_MENSAGEM_PCK.get_texto(278979);
elsif (ie_tipo_log_p = 'E') then 
	ds_observacao_w:= WHEB_MENSAGEM_PCK.get_texto(278980);
elsif (ie_tipo_log_p = 'R') then 
	ds_observacao_w:= WHEB_MENSAGEM_PCK.get_texto(278981);
elsif (ie_tipo_log_p = 'D') then 
	ds_observacao_w:= WHEB_MENSAGEM_PCK.get_texto(278983);
elsif (ie_tipo_log_p = 'A') then 
	ds_observacao_w:= WHEB_MENSAGEM_PCK.get_texto(278984);
elsif (ie_tipo_log_p = 'P') then 
	ds_observacao_w:= WHEB_MENSAGEM_PCK.get_texto(278985);
elsif (ie_tipo_log_p = 'G') then 
	ds_observacao_w:= WHEB_MENSAGEM_PCK.get_texto(278986);
end if;
 
insert into protocolo_convenio_log(nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_seq_protocolo, 
		ds_arquivo_envio, 
		vl_protocolo, 
		ie_tipo_log, 
		ds_observacao) 
	values (nextval('protocolo_convenio_log_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_seq_protocolo_p, 
		ds_arquivo_envio_w, 
		vl_protocolo_w, 
		ie_tipo_log_p, 
		ds_observacao_w);
		 
<<final>> 
 
commit;
 
end;
