CREATE OR REPLACE PROCEDURE gravar_log_tasy_prag ( cd_log_p bigint, ds_log_p text, nm_usuario_p text DEFAULT null) AS $body$
DECLARE
	-- Change this to reflect the dblink connection string
	v_conn_str  text := format('port=%s dbname=%s user=%s', current_setting('port'), current_database(), current_user);
	v_query     text;

BEGIN
	v_query := 'CALL gravar_log_tasy_prag_atx ( ' || quote_nullable(cd_log_p) || ',' || quote_nullable(ds_log_p) || ',' || quote_nullable(nm_usuario_p) || ' )';
