CREATE OR REPLACE PROCEDURE gravar_log_tasy_prag_atx ( cd_log_p bigint, ds_log_p text, nm_usuario_p text DEFAULT null) AS $body$
DECLARE

dt_fim_geracao_w	timestamp;
BEGIN
begin
	select	coalesce(DT_FIM_GERACAO, clock_timestamp())
	into STRICT	dt_fim_geracao_w
	from	tipo_log_tasy
	where	cd_log = cd_log_p;
exception
	When no_data_found then
		dt_fim_geracao_w := clock_timestamp();
end;

if	Trunc(dt_fim_geracao_w) >= Trunc(clock_timestamp()) then
	insert into log_tasy(  dt_atualizacao,
				nm_usuario,
				cd_log,
				ds_log)
		values ( 	clock_timestamp(),
				coalesce(nm_usuario_p,coalesce(wheb_usuario_pck.get_nm_usuario,'Tasy')),
				cd_log_p,
				substr(ds_log_p,1,2000));

end if;

end;
$body$
LANGUAGE PLPGSQL
