CREATE OR REPLACE PROCEDURE gravar_mat_int_opme ( cd_material_p bigint, qt_unitaria_p bigint, cd_fornecedor_p text, nr_seq_agenda_p bigint, nr_seq_prescricao_p bigint, cd_motivo_baixa_p bigint, ie_acao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_agenda_w		bigint;
qt_existe_w		varchar(1);
nr_seq_opme_w		bigint;
nr_atendimento_w	bigint;
nr_seq_tipo_baixa_w	bigint;
qt_material_total_w	double precision;
qt_material_atual_W 	double precision;
qt_itens_novo_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	agenda_pac_opme
where	cd_material = cd_material_p
and	nr_seq_agenda = nr_seq_agenda_p;

if (ie_acao_p = 1) and (coalesce(qt_existe_w,0) = 0) then
	begin
	select 	nextval('agenda_pac_opme_seq')
	into STRICT	nr_sequencia_w
	;

	insert into agenda_pac_opme(
		cd_cgc,
		cd_cond_pagto,
		cd_material,
		ds_observacao,
		dt_atualizacao,
		dt_atualizacao_nrec,
		dt_exclusao,
		ie_autorizado,
		ie_gerar_autor,
		ie_integracao,
		ie_integracao_util,
		ie_origem_inf,
		ie_padrao,
		nm_usuario,
		nm_usuario_exclusao,
		nm_usuario_nrec,
		nr_seq_agenda,
		nr_seq_apres,
		nr_seq_motivo_exclusao,
		nr_seq_proc_interno,
		nr_sequencia,
		qt_material,
		vl_desconto,
		vl_unitario_item,
		nr_seq_prescricao)
	values (cd_fornecedor_p,
		'',
		cd_material_p,
		'',
		clock_timestamp(),
		clock_timestamp(),
		'',
		'P',
		'N',
		'S',
		'AC',
		'I',
		'S',
		nm_usuario_p,
		'',
		nm_usuario_P,
		nr_seq_agenda_p,
		500,
		null,
		null,
		nr_sequencia_w,
		qt_unitaria_p,
		'',
		'',
		nr_seq_prescricao_p);
	end;

	select 	nr_atendimento
	into STRICT	nr_atendimento_w
	from 	cirurgia
	where 	nr_seq_agenda = nr_seq_agenda_p;

	select	nr_sequencia
	into STRICT	nr_seq_tipo_baixa_w
	from	tipo_baixa_prescricao
	where	cd_tipo_baixa = cd_motivo_baixa_p
	and 	ie_prescricao_devolucao = 'P';

	update 	material_atend_paciente
	set 	nr_seq_agenda_pac_opme = nr_sequencia_w,
		nr_seq_tipo_baixa = nr_seq_tipo_baixa_w
	where	nr_atendimento = nr_atendimento_w
	and 	cd_material = cd_material_p;


elsif (coalesce(qt_existe_w,0) > 0) then

	select	qt_material,
		nr_sequencia
	into STRICT	qt_material_atual_W,
		nr_sequencia_w
	from	agenda_pac_opme
	where 	cd_material = cd_material_p
	and	nr_seq_agenda = nr_seq_agenda_p;

	qt_material_total_w := qt_material_atual_W + qt_unitaria_p;

	update	agenda_pac_opme
	set	qt_material = qt_material_total_w
	where 	cd_material = cd_material_p
	and	nr_seq_agenda = nr_seq_agenda_p;

	select 	nr_atendimento
	into STRICT	nr_atendimento_w
	from 	cirurgia
	where 	nr_seq_agenda = nr_seq_agenda_p;

	select	nr_sequencia
	into STRICT	nr_seq_tipo_baixa_w
	from	tipo_baixa_prescricao
	where	cd_tipo_baixa = cd_motivo_baixa_p
	and 	ie_prescricao_devolucao = 'P';

	update 	material_atend_paciente
	set 	nr_seq_agenda_pac_opme = nr_sequencia_w,
		nr_seq_tipo_baixa = nr_seq_tipo_baixa_w
	where	nr_atendimento = nr_atendimento_w
	and 	cd_material = cd_material_p;


end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gravar_mat_int_opme ( cd_material_p bigint, qt_unitaria_p bigint, cd_fornecedor_p text, nr_seq_agenda_p bigint, nr_seq_prescricao_p bigint, cd_motivo_baixa_p bigint, ie_acao_p bigint, nm_usuario_p text) FROM PUBLIC;

