CREATE OR REPLACE PROCEDURE gravar_motivo_duplic_cpf_pf ( cd_pessoa_fisica_p text, nr_seq_motivo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gravar o motivo de duplicidade do CPF para a pessoa física.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_motivo_w			bigint;


BEGIN
select	count(1)
into STRICT	qt_motivo_w
from	pessoa_fisica_aux	a
where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (qt_motivo_w = 0) then
	insert into pessoa_fisica_aux(	cd_pessoa_fisica,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_motivo,
			nr_sequencia)
	values (	cd_pessoa_fisica_p,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			nr_seq_motivo_p,
			nextval('pessoa_fisica_aux_seq'));
else
	update	pessoa_fisica_aux	a
	set	nr_seq_motivo		= nr_seq_motivo_p
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
