CREATE OR REPLACE PROCEDURE gravar_nota_fiscal_consist ( nr_seq_nota_p bigint, ds_consistencia_p text, ie_forma_consistencia_p text, ie_nota_item_p text, ds_observacao_p text, nm_usuario_p text, ie_commit_p text, nr_item_nf_p bigint default null) AS $body$
DECLARE


ie_sistema_origem_w  nota_fiscal.ie_sistema_origem%type;


BEGIN

insert into nota_fiscal_consist(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_nota,
	ie_forma_consistencia,
	ie_nota_item,
	ds_consistencia,
	ds_observacao,
	nr_item_nf)
values (	nextval('nota_fiscal_consist_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_nota_p,
	ie_forma_consistencia_p,
	ie_nota_item_p,
	ds_consistencia_p,
	ds_observacao_p,
	nr_item_nf_p);

select	a.ie_sistema_origem
into STRICT	ie_sistema_origem_w
from 	nota_fiscal a
where 	a.nr_sequencia = nr_seq_nota_p;

if (ie_commit_p = 'S' and coalesce(ie_sistema_origem_w,'XX') <> 'INTEGRACAO' ) then
	commit;
end if;

end;
$body$
