CREATE OR REPLACE PROCEDURE gravar_novo_segmento_item_nc ( nr_sequencia_p bigint, nr_seq_segmento_atual_p bigint, nm_usuario_p text) AS $body$
DECLARE



ie_curva_atual_w	varchar(1);
ie_curva_abc_segmento_w	varchar(1);

BEGIN

select	a.ie_curva_atual
into STRICT	ie_curva_atual_w
from	nc_curva_abc_item a
where	a.nr_sequencia		= nr_sequencia_p;

select	ie_curva_abc
into STRICT	ie_curva_abc_segmento_w
from	segmento_compras
where	nr_sequencia = nr_seq_segmento_atual_p;


if (ie_curva_atual_w IS NOT NULL AND ie_curva_atual_w::text <> '') and
	((ie_curva_abc_segmento_w = 'T') or (ie_curva_abc_segmento_w = ie_curva_atual_w)) then

	update	nc_curva_abc_item
	set	nr_seq_segmento_atual	= nr_seq_segmento_atual_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
