CREATE OR REPLACE PROCEDURE gravar_observacao_oc ( nr_ordem_compra_p bigint, ds_observacao_p text) AS $body$
DECLARE

 
ds_observacao_w			varchar(4000);


BEGIN 
 
select	ds_observacao 
into STRICT	ds_observacao_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
if (coalesce(ds_observacao_w::text, '') = '') then 
	ds_observacao_w	:=	substr(ds_observacao_p,1,4000);
else 
	ds_observacao_w	:=	substr(ds_observacao_w || chr(13) || chr(10) || ds_observacao_p,1,4000);
end if;
 
update	ordem_compra 
set	ds_observacao	= ds_observacao_w 
where	nr_ordem_compra	= nr_ordem_compra_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
