CREATE OR REPLACE PROCEDURE gravar_observacao_prescr_mat ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, ds_observacao_p text, ie_campo_p text, nm_usuario_p text) AS $body$
DECLARE


/* #######  IE_CAMPO_P   #########	*/

/*     1	-  DS_OBSERVACAO_CCIH	*/

/*     2	-  DS_OBSERVACAO		*/

/* ##########################	*/

BEGIN

if (ie_campo_p	= '1') then
	update  prescr_material
	set    ds_observacao_ccih	= substr(ds_observacao_p,1,4000)
	where  nr_sequencia		= nr_seq_prescr_p
	and    nr_prescricao		= nr_prescricao_p;
elsif (ie_campo_p	= '2') then
	update  prescr_material
	set    ds_observacao		= substr(ds_observacao_p,1,4000)
	where  nr_sequencia		= nr_seq_prescr_p
	and    nr_prescricao		= nr_prescricao_p;
end if;

commit;

end;
$body$
