SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_obs_proced ( nr_prescricao_p bigint, nr_sequencia_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;


BEGIN


if (nr_prescricao_p > 0) and (nr_sequencia_p > 0) then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	prescr_procedimento_obs
	where	nr_prescricao		= nr_prescricao_p
	and	nr_seq_prescricao	= nr_sequencia_p;

	if (nr_sequencia_w > 0) then
		if (ds_observacao_p <> '') or (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then
			update	prescr_procedimento_obs
			set	ds_observacao		= substr(ds_observacao_p,1,2000),
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
			where	nr_prescricao		= nr_prescricao_p
			and	nr_seq_prescricao	= nr_sequencia_p;
		else
			delete	FROM prescr_procedimento_obs
			where	nr_prescricao		= nr_prescricao_p
			and	nr_seq_prescricao	= nr_sequencia_p;
		end if;
	else
		if (ds_observacao_p <> '') or (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then

			select	nextval('prescr_procedimento_obs_seq')
			into STRICT	nr_sequencia_w
			;

			insert into prescr_procedimento_obs(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_prescricao,
				nr_seq_prescricao,
				ds_observacao)
			values (
				nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_prescricao_p,
				nr_sequencia_p,
				substr(ds_observacao_p,1,2000));
		end if;
	end if;

	commit;

	end;
end if;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
