CREATE OR REPLACE PROCEDURE gravar_ordem_producao_opm ( nr_seq_opm_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_lista_seq_opm_p text default null, qt_opm_p bigint default 1) AS $body$
DECLARE

						 
c01 CURSOR FOR 
	SELECT 	cd_material, 
		cd_material_padrao, 
		coalesce(qt_material,1) 
	from  	prescr_opm_componente 
	where 	nr_seq_opm = nr_seq_opm_p;						
						 
nr_sequencia_w		bigint;
cd_material_w		bigint;
cd_material_padrao_w	bigint;
qt_material_w		double precision;	

BEGIN 
 
CALL consiste_permissao_gerar_op(nr_seq_opm_p,nm_usuario_p,cd_estabelecimento_p);
 
select	nextval('ordem_producao_opm_seq') 
into STRICT	nr_sequencia_w
;
 
insert into ordem_producao_opm(nr_sequencia, 
				dt_atualizacao, 
				nm_usuario, 
				dt_atualizacao_nrec, 
				nm_usuario_nrec, 
				nr_seq_opm, 
				ie_situacao, 
				dt_inativacao, 
				nm_usuario_inativacao,					 
				cd_estabelecimento, 
      				qt_opm) 
values (nr_sequencia_w, 
				clock_timestamp(), 
				nm_usuario_p, 
				clock_timestamp(), 
				nm_usuario_p, 
				nr_seq_opm_p, 
				'A', 
				null, 
				null,					 
				cd_estabelecimento_p, 
      				qt_opm_p);
				 
open c01;
loop 
fetch c01 into	 
	cd_material_w, 
	cd_material_padrao_w, 
	qt_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	insert into ordem_producao_comp_opm(nr_sequencia, 
						dt_atualizacao, 
						nm_usuario, 
						dt_atualizacao_nrec, 
						nm_usuario_nrec, 
						nr_seq_ordem_opm, 
						nr_seq_lote_fornec, 
						cd_material_padrao, 
						cd_material, 
						qt_material, 
						ie_status) 
	values					(nextval('ordem_producao_comp_opm_seq'), 
						clock_timestamp(), 
						nm_usuario_p, 
						clock_timestamp(), 
						nm_usuario_p, 
						nr_sequencia_w, 
						null, 
						cd_material_w, 
						cd_material_padrao_w, 
						(qt_material_w * coalesce(qt_opm_p,1)), 
						'G');
	end;
end loop;
close c01;
 
if (ds_lista_seq_opm_p IS NOT NULL AND ds_lista_seq_opm_p::text <> '') then 
  update PRESCR_OPM set NR_ORDEM_OPM = nr_sequencia_w, dt_atualizacao = clock_timestamp(), nm_usuario = nm_usuario_p 
  where Obter_Se_Contido(nr_sequencia,ds_lista_seq_opm_p) = 'S';
end if;
 
commit;
				 
CALL gravar_status_op_opm(nr_sequencia_w,'GOP',null,nm_usuario_p,cd_estabelecimento_p);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
