CREATE OR REPLACE PROCEDURE gravar_painel_usuario ( nr_seq_indicador_p bigint, nm_usuario_p text, ie_opcao_p text, cd_perfil_p bigint) AS $body$
DECLARE


qt_registro_w	bigint;
saveForProfile	boolean;


BEGIN

qt_registro_w := 0;
saveForProfile := (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '' AND cd_perfil_p <> 0);

if (ie_opcao_p = 'A') then

	if	saveForProfile then
		select	count(*)
		into STRICT	qt_registro_w
		from	indicador_gestao_usuario
		where	nr_seq_indicador = nr_seq_indicador_p
		and	cd_perfil = cd_perfil_p;
	else
		select	count(*)
		into STRICT	qt_registro_w
		from	indicador_gestao_usuario
		where	nr_seq_indicador = nr_seq_indicador_p
		and	nm_usuario = nm_usuario_p;
	end if;

	if (qt_registro_w = 0) then
		if	saveForProfile then
			insert into indicador_gestao_usuario(
				nr_sequencia,
				dt_atualizacao,
				cd_perfil,
				nr_seq_indicador,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (
				nextval('indicador_gestao_usuario_seq'),
				clock_timestamp(),
				cd_perfil_p,
				nr_seq_indicador_p,
				clock_timestamp(),
				nm_usuario_p);
		else
			insert into indicador_gestao_usuario(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				nr_seq_indicador,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (
				nextval('indicador_gestao_usuario_seq'),
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_indicador_p,
				clock_timestamp(),
				nm_usuario_p);
		end if;
	end if;

elsif (ie_opcao_p = 'E') then
	if	saveForProfile then
		delete	from indicador_gestao_usuario
		where	nr_seq_indicador	=	nr_seq_indicador_p
		and	cd_perfil		=	cd_perfil_p;
	else
		delete	from indicador_gestao_usuario
		where	nr_seq_indicador	=	nr_seq_indicador_p
		and	nm_usuario		=	nm_usuario_p;
	end if;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
