SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_param_relat_auto ( nr_seq_rel_auto_p bigint, cd_parametro_p text, ds_valor_param_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_relatorio_w		bigint;
nr_seq_param_w		bigint;



BEGIN

if (coalesce(cd_parametro_p::text, '') = '') then

	delete	from relatorio_auto_param
	where	nr_seq_rel_auto	= nr_seq_rel_auto_p;

	select	max(nr_seq_relatorio)
	into STRICT	nr_seq_relatorio_w
	from	relatorio_auto_imp
	where	nr_sequencia	= nr_seq_rel_auto_p;

	insert	into relatorio_auto_param(
		nr_sequencia,
		nr_seq_rel_auto,
		cd_parametro,
		dt_atualizacao,
		nm_usuario,
		ds_valor)
	SELECT	nextval('relatorio_auto_param_seq'),
		nr_seq_rel_auto_p,
		cd_parametro,
		clock_timestamp(),
		nm_usuario_p,
		vl_padrao
	from	relatorio_parametro
	where	nr_seq_relatorio	= nr_seq_relatorio_w;
else
	update	relatorio_auto_param
	set	ds_valor		= ds_valor_param_p
	where	nr_seq_rel_auto	= nr_seq_rel_auto_p
	and	cd_parametro		= cd_parametro_p;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
