CREATE OR REPLACE PROCEDURE gravar_pend_autorizacao_atend (nr_atendimento_p bigint) AS $body$
DECLARE

 
ds_pend_autor_w		varchar(30);


BEGIN 
 
 
select	substr(obter_pendencia_guia_atend(nr_atendimento_p),1,30) 
into STRICT	ds_pend_autor_w
;
 
update	atendimento_paciente 
set 	ds_pend_autorizacao	= ds_pend_autor_w 
where	nr_atendimento	= nr_atendimento_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
