CREATE OR REPLACE PROCEDURE gravar_posicao_painel ( nr_seq_indicador_p bigint, nm_usuario_p text, qt_topo_p bigint, qt_esquerda_p bigint, qt_altura_p bigint, qt_largura_p bigint, ds_tam_campo_p text, cd_perfil_p bigint) AS $body$
BEGIN

if (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '' AND cd_perfil_p <> 0) then
	update	indicador_gestao_usuario
	set	qt_topo			= qt_topo_p,
		qt_esquerda		= qt_esquerda_p,
		qt_altura		= qt_altura_p,
		qt_largura		= qt_largura_p,
		ds_tam_campo		= substr(ds_tam_campo_p,1,255)
	where	nr_seq_indicador	= nr_seq_indicador_p
	and	cd_perfil		= cd_perfil_p;
else
	update	indicador_gestao_usuario
	set	qt_topo			= qt_topo_p,
		qt_esquerda		= qt_esquerda_p,
		qt_altura		= qt_altura_p,
		qt_largura		= qt_largura_p,
		ds_tam_campo		= substr(ds_tam_campo_p,1,255)
	where	nr_seq_indicador	= nr_seq_indicador_p
	and	nm_usuario		= nm_usuario_p;
end if;

commit;

end;
$body$
