CREATE OR REPLACE PROCEDURE gravar_prioridade_lista_espera (nr_seq_prioridade_p bigint, nr_seq_motivo_p bigint, ds_justificativa_p text, ds_observacao_p text, nr_sequencia_p bigint) AS $body$
DECLARE


nr_seq_ant_prior_w 		alteracao_lista_espera.nr_seq_prioridade%type;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

		select 	max(nr_seq_prioridade)
		into STRICT 	nr_seq_ant_prior_w
		from 	agenda_lista_espera
		where 	nr_sequencia = nr_sequencia_p;

		update agenda_lista_espera
		set nr_seq_prioridade = nr_seq_prioridade_p
		where nr_sequencia = nr_sequencia_p;



		insert into alteracao_lista_espera(nr_sequencia,
											nr_seq_prioridade_ant,
											nr_seq_prioridade,
											nr_seq_motivo,
											nr_seq_agenda,
											nm_usuario_nrec,
											nm_usuario,
											dt_atualizacao_nrec,
											dt_atualizacao,
											dt_alteracao,
											ds_observacao,
											ds_justificativa)
		values (nextval('alteracao_lista_espera_seq'),
											nr_seq_ant_prior_w,
											nr_seq_prioridade_p,
											nr_seq_motivo_p,
											nr_sequencia_p,
											obter_usuario_ativo,
											obter_usuario_ativo,
											clock_timestamp(),
											clock_timestamp(),
											clock_timestamp(),
											ds_observacao_p,
											ds_justificativa_p);

end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
