CREATE OR REPLACE PROCEDURE gravar_registro_resumo_oft ( ds_item_p text, nr_seq_item_p bigint, nm_usuario_p text, dt_registro_p timestamp default null) AS $body$
DECLARE

nr_sequencia_w		bigint;


BEGIN

select	nextval('w_oft_resumo_seq')
