	nm_usuario,
	nm_usuario_nrec,
	nr_seq_item,
	nr_sequencia,
	dt_registro)
values (ds_item_p,
	clock_timestamp(),
	clock_timestamp(),
	nm_usuario_p,
	nm_usuario_p,
	nr_seq_item_p,
	nr_sequencia_w,
	dt_registro_p);

commit;

end if;

end;
