CREATE OR REPLACE PROCEDURE gravar_regulacao_info_adic (nr_regulacao_p bigint, ds_info_adicional_p text, nr_esclarecimento_regulacao_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_sequencia_regulacao_w	bigint;
nr_sequencia_info_adic_w        bigint;

BEGIN
        -- procura na tabela REGULACAO_ATENDIMENTO o NR_SEQUENCIA
	select 	max(nr_sequencia)
	into STRICT 	nr_sequencia_regulacao_w
        from   	regulacao_atendimento
        where  	nr_regulacao = nr_regulacao_p;


	-- se achou, insere registro
	if (nr_sequencia_regulacao_w IS NOT NULL AND nr_sequencia_regulacao_w::text <> '') then

	        -- verifica se j√° existe registro do esclarecimento
		select 	max(nr_sequencia)
		into STRICT    nr_sequencia_info_adic_w
		from   	regulacao_info_adicional
		where  	nr_seq_regulacao_atendimento = nr_sequencia_regulacao_w
		and	nr_esclarecimento_regulacao = nr_esclarecimento_regulacao_p;

		if 	((coalesce(nr_sequencia_info_adic_w::text, '') = '') or (nr_esclarecimento_regulacao_p = -1)) then
			insert into regulacao_info_adicional(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ds_info_adicional,
				nr_esclarecimento_regulacao,
				cd_profissional,
				nr_seq_regulacao_atendimento,
				dt_liberacao
			) values (
				nextval('regulacao_info_adicional_seq'),
				clock_timestamp(),
				coalesce(nm_usuario_p, 'Regulacao'),
				clock_timestamp(),
				coalesce(nm_usuario_p, 'Regulacao'),
				ds_info_adicional_p,
				nr_esclarecimento_regulacao_p,
				null,
				nr_sequencia_regulacao_w,
				clock_timestamp()
			);
		end if;
	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
