SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_result_parcial_exa (nr_prescricao_p text, nr_seq_prescr_p text, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE

ie_existe_w	varchar(2);
nr_sequencia_w	varchar(255);

BEGIN

ie_existe_w := existe_result_parcial(nr_prescricao_p,nr_seq_prescr_p);

begin
if (ie_existe_w = 'S') then
	select	nr_sequencia
	into STRICT	nr_sequencia_w
	from	exame_lab_result_parcial
	where	nr_prescricao = nr_prescricao_p
	and	nr_seq_prescr = nr_seq_prescr_p;

	update exame_lab_result_parcial set
					dt_atualizacao = clock_timestamp(),
					nm_usuario = nm_usuario_p,
					ds_resultado_parcial = ds_observacao_p	,
					dt_resultado_parcial = clock_timestamp()
	where	nr_sequencia = nr_sequencia_w;

elsif (ie_existe_w = 'N') then
	insert into exame_lab_result_parcial(nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_prescricao,
					nr_seq_prescr,
					ds_resultado_parcial,
					dt_resultado_parcial)
	values (nextval('exame_lab_result_parcial_seq'),
				        clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_prescricao_p,
					nr_seq_prescr_p,
					ds_observacao_p,
					clock_timestamp());
end if;
commit;

exception
when others then
	rollback;
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(279318);
end;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
