CREATE OR REPLACE PROCEDURE gravar_tarja_cartao_autor ( nr_sequencia_p bigint, ds_tarja_cartao_p text, nm_usuario_p text) AS $body$
DECLARE


ds_tarja_cartao_w	varchar(4000);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) and (coalesce(ds_tarja_cartao_p,'X') <> 'X') then
	begin
	ds_tarja_cartao_w := substr(ds_tarja_cartao_p,1,4000);

	update	autorizacao_convenio
	set	ds_tarja_cartao	= ds_tarja_cartao_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
	commit;
	end;
end if;

end;
$body$
