CREATE OR REPLACE PROCEDURE gravar_usuario_alt_regulacao (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_sequencia_p > 0) then
	if (coalesce(nm_usuario_p::text, '') = '') or (nm_usuario_p = '') then
		update 	eme_regulacao
		set		nm_usuario_alt	= nm_usuario_p,
				dt_bloqueio	 = NULL
		where	nr_sequencia	= nr_sequencia_p;
	else
		update 	eme_regulacao
		set		nm_usuario_alt	= nm_usuario_p,
				dt_bloqueio	= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
