CREATE OR REPLACE PROCEDURE gravar_w_titulo_abatimento ( nr_titulo_p bigint, nr_titulo_ref_p bigint, vl_abatimento_p bigint, ie_pagar_receber_p text, nm_usuario_p text, nr_bordero_p bigint) AS $body$
BEGIN
if	((nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') or (nr_bordero_p IS NOT NULL AND nr_bordero_p::text <> '')) and (nr_titulo_ref_p IS NOT NULL AND nr_titulo_ref_p::text <> '') and (vl_abatimento_p IS NOT NULL AND vl_abatimento_p::text <> '') then
	begin
	insert into w_titulo_abatimento(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_titulo,
		ie_pagar_receber,
