	values (nextval('w_titulo_abatimento_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_titulo_p,
		ie_pagar_receber_p,
		nr_titulo_ref_p,
		vl_abatimento_p,
		nr_bordero_p);

	update	titulo_receber
	set	vl_abatimento	= 0
	where	nr_titulo	= nr_titulo_ref_p;

