SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_w_titulo_selecionado ( nr_titulo_p bigint, ie_limpar_selecao_p text, nm_usuario_p text) AS $body$
DECLARE

/*
S =	Todos
SE = 	Selecionados
*/
qtd_reg	bigint;

BEGIN
if (ie_limpar_selecao_p = 'S') then
	delete	FROM w_titulo_selecionado
	where	nm_usuario = nm_usuario_p;

elsif (ie_limpar_selecao_p = 'SE') then
	delete	FROM w_titulo_selecionado
	where	nm_usuario = nm_usuario_p
	and	nr_titulo  = nr_titulo_p;
else
	begin --AAMFIRMO OS 544900 13/03/2013 - Para nao duplicar os titulos  ja inclusos.
	select	count(*)
	into STRICT	qtd_reg
	from	w_titulo_selecionado
	where	nm_usuario = nm_usuario_p
	and	nr_titulo  = nr_titulo_p;
	exception when no_data_found then
		qtd_reg := 0;
	end;

	if ( qtd_reg = 0 ) then
		insert into w_titulo_selecionado(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_titulo)
		values (	nextval('w_titulo_selecionado_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_titulo_p);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
