SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gravar_zpl_etiqueta ( nr_seq_lab_etiqueta_p bigint, zpl_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_lab_etiqueta_p IS NOT NULL AND nr_seq_lab_etiqueta_p::text <> '') then

	delete
	from	lab_etiqueta_conting_zpl
	where	nr_seq_etiqueta_contingencia = nr_seq_lab_etiqueta_p;


	insert	into lab_etiqueta_conting_zpl(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_etiqueta_contingencia,
		ds_zpl
		)
	values (
		nextval('lab_etiqueta_conting_zpl_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_lab_etiqueta_p,
		zpl_p
		);



	update	lab_etiqueta_contingencia
	set	dt_atualizacao 	= clock_timestamp(),
		dt_envio 	= clock_timestamp(),
		nm_usuario 	= nm_usuario_p
	where	nr_sequencia =  nr_seq_lab_etiqueta_p;


	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
