CREATE OR REPLACE PROCEDURE grava_avisos_oft (cd_pessoa_fisica_p text, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w 	bigint;
nr_sequencia_w 	bigint;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	 select  count(*)
	 into STRICT qt_registro_w
	 from oft_observacao_paciente
	 where cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (coalesce(qt_registro_w,0) = 0) then
		select nextval('oft_observacao_paciente_seq')
		into STRICT nr_sequencia_w
		;

		insert into oft_observacao_paciente(
			nr_sequencia,
			cd_pessoa_fisica,
			ds_observacao,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec)
		values (nr_sequencia_w,
			cd_pessoa_fisica_p,
			ds_observacao_p,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p);
	else
		update  oft_observacao_paciente
		set 	ds_observacao = ds_observacao_p
		where 	cd_pessoa_fisica = cd_pessoa_fisica_p;

		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
