CREATE OR REPLACE PROCEDURE grava_campos_nom ( nr_linha_p bigint ,nm_campo_p text ,ds_valor_p text ,guia_p bigint ,nm_usuario_p text ) AS $body$
DECLARE


ie_criptografa_w  varchar(1);
nr_sequencia_w bigint;

nr_seq_log_w bigint;


BEGIN

  select max(coalesce(ie_confidencial,'N'))
  into STRICT ie_criptografa_w
  from validador_nom_campo
  where nr_seq_validador = guia_p
  and upper(ds_campo) = upper(nm_campo_p);

  if (nm_campo_p IS NOT NULL AND nm_campo_p::text <> '') then
  begin
      select nextval('w_geracao_guia_seq')
      into STRICT nr_sequencia_w
;

      insert into w_geracao_guia(nr_sequencia, nm_usuario, nr_linha, ds_coluna, ds_valor, ds_valor_criptografado, ie_confidencial, id_sessao, ie_tipo_guia)
      values (nr_sequencia_w, nm_usuario_p, nr_linha_p, nm_campo_p, ds_valor_p, null, coalesce(ie_criptografa_w,'N'), null, guia_p);
      commit;
  end;
  end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
