CREATE OR REPLACE PROCEDURE grava_data_impressao_exame (nr_prescricao_p bigint, nr_seq_prescr_p text) AS $body$
BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	update 	exame_lab_result_item
	set 	dt_impressao 	 = clock_timestamp()
	where	nr_seq_resultado = (SELECT 	max(nr_seq_resultado)
				    from	exame_lab_resultado
				    where	nr_prescricao = nr_prescricao_p)
	and	obter_se_contido(nr_seq_prescr, nr_seq_prescr_p) = 'S'
	and	coalesce(dt_impressao::text, '') = ''
	and	(nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');

	CALL gravar_log_tasy(80007,'nr_prescricao: '||nr_prescricao_p||'nr_seq_prescr: '||nr_seq_prescr_p,'tasy');

end if;

commit;

end;
