CREATE OR REPLACE PROCEDURE grava_data_impr_exame_lote (nr_seq_lote_p bigint) AS $body$
DECLARE



nr_sequencia_w integer;
nr_prescricao_w bigint;


C01 CURSOR FOR
	SELECT a.nr_sequencia,
		   a.nr_prescricao
	from prescr_procedimento a,
		 lab_lote_exame 	 c,
		 lab_lote_exame_item b
	where a.nr_prescricao = b.nr_prescricao
	and	  a.nr_sequencia  = b.nr_seq_prescr
	and	  b.nr_seq_lote   = c.nr_sequencia
	and	  b.nr_seq_lote   = nr_seq_lote_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		update 	prescr_procedimento
		set	   	dt_imp_etiq_coleta = clock_timestamp()
		where 	nr_prescricao = nr_prescricao_w
		and		nr_sequencia  = nr_sequencia_w;
	end;
end loop;
close C01;

commit;

end;
