CREATE OR REPLACE PROCEDURE grava_email_usuario (dt_mensagem_p timestamp, nr_seq_pasta_p bigint, ds_email_p text, ds_assunto_p text, ds_mensagem_p text, ds_anexo_p text, ie_entrada_saida_p text, nm_usuario_p text) AS $body$
