	ds_email_p,
	nr_seq_pasta_p,
	ds_anexo_p);

if (ds_mensagem_p IS NOT NULL AND ds_mensagem_p::text <> '') then
	begin
	insert	into usuario_correio_mensagem(nr_seq_correio,
			ds_mensagem)
