CREATE OR REPLACE PROCEDURE grava_etapa_gedipa_html5 ( nr_prescricao_p bigint, nr_seq_cpoe_p bigint, ie_conferencia_p text, ie_higienizacao_p text, ie_preparo_p text, nm_usuario_p text) AS $body$
DECLARE

 
ie_intervencao_w		varchar(1);
nr_sequencia_w			integer;
ie_agrupador_w			smallint;
ds_material_w			varchar(255);

c01 CURSOR FOR 
SELECT	nr_sequencia, 
	ie_agrupador, 
	substr(obter_desc_material(cd_material),1,255) 
from	prescr_material 
where	nr_prescricao		= nr_prescricao_p 
and	nr_seq_mat_cpoe		= nr_seq_cpoe_p;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	ie_agrupador_w, 
	ds_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	if (ie_agrupador_w	= 1) then 
		select	coalesce(max(ie_intervencao_preparo),'N') 
		into STRICT	ie_intervencao_w 
		from	adep_area_prep 
		where	nr_sequencia = obter_area_preparo(nr_prescricao_p,nr_sequencia_w);
		if (ie_intervencao_w	= 'N') then 
			--Não é possível gerar intervenção do preparo para a área do medicamento! 
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(38534);
		end if;
	end if;
	 
	update	prescr_material 
	set	ie_conferencia		= CASE WHEN ie_conferencia_p='N' THEN 'N'  ELSE null END , 
		ie_higienizacao		= CASE WHEN ie_higienizacao_p='N' THEN 'N'  ELSE null END , 
		ie_preparo		= CASE WHEN ie_preparo_p='N' THEN 'N'  ELSE null END , 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp() 
	where	nr_prescricao		= nr_prescricao_p 
	and	nr_sequencia		= nr_sequencia_w;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
