CREATE OR REPLACE PROCEDURE grava_etapa_item_gedipa ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_agrupador_p bigint, ie_conferencia_p text, ie_higienizacao_p text, ie_preparo_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_agrupador_p = 1) then

	update	prescr_material
	set	ie_conferencia		=	CASE WHEN ie_conferencia_p='N' THEN 'N'  ELSE null END ,
		ie_higienizacao		=	CASE WHEN ie_higienizacao_p='N' THEN 'N'  ELSE null END ,
		ie_preparo		=	CASE WHEN ie_preparo_p='N' THEN 'N'  ELSE null END ,
		nm_usuario		=	nm_usuario_p,
		dt_atualizacao		=	clock_timestamp()
	where	nr_prescricao		=	nr_prescricao_p
	and	nr_agrupamento		=	(	SELECT	max(nr_agrupamento)
							from	prescr_material
							where	nr_prescricao	=	nr_prescricao_p
							and	nr_sequencia	=	nr_sequencia_p);
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
