CREATE OR REPLACE PROCEDURE grava_etapa_processo_gedipa ( nr_seq_processo_p bigint, ie_conferencia_p text, ie_higienizacao_p text, ie_preparo_p text, nr_seq_area_prep_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_existe_w		bigint;


BEGIN

select  count(*)
into STRICT	ie_existe_w
from 	gedipa_intervencao_preparo
where	nr_seq_processo		=	nr_seq_processo_p
and 	nr_seq_area_prep	=	nr_seq_area_prep_p;

select	nextval('gedipa_intervencao_preparo_seq')
into STRICT	nr_sequencia_w
;

if (ie_existe_w = 0) then
	insert into gedipa_intervencao_preparo(
		nr_sequencia,
		ie_conferencia,
		ie_higienizacao,
		ie_preparo,
		nm_usuario,
		nr_seq_area_prep,
		dt_atualizacao,
		nr_seq_processo)
	values (nr_sequencia_w,
		CASE WHEN ie_conferencia_p='N' THEN 'N'  ELSE null END ,
		CASE WHEN ie_higienizacao_p='N' THEN 'N'  ELSE null END ,
		CASE WHEN ie_preparo_p='N' THEN 'N'  ELSE null END ,
		nm_usuario_p,
		nr_seq_area_prep_p,
		clock_timestamp(),
		nr_seq_processo_p);
else
	update	gedipa_intervencao_preparo
	set	ie_conferencia		=	CASE WHEN ie_conferencia_p='N' THEN 'N'  ELSE null END ,
		ie_higienizacao		=	CASE WHEN ie_higienizacao_p='N' THEN 'N'  ELSE null END ,
		ie_preparo		=	CASE WHEN ie_preparo_p='N' THEN 'N'  ELSE null END ,
		nm_usuario		=	nm_usuario_p,
		dt_atualizacao		=	clock_timestamp()
	where	nr_seq_processo		=	nr_seq_processo_p
	and 	nr_seq_area_prep	=	nr_seq_area_prep_p;
end if;

