SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE grava_hist_email_oc_nao_aprov ( nr_ordem_compra_p bigint, nr_seq_motivo_p text, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


ds_motivo_w			varchar(255);
ds_titulo_w			varchar(80);
ds_mensagem_w			varchar(4000);


BEGIN

select	ds_motivo
into STRICT	ds_motivo_w
from	motivo_email_nao_aprovados
where	nr_Sequencia = nr_seq_motivo_p;

ds_titulo_w 	:= 	wheb_mensagem_pck.get_Texto(802160);
ds_mensagem_w	:= 	substr(wheb_mensagem_pck.get_Texto(802162, 'DS_MOTIVO_W='|| DS_MOTIVO_W),1,4000);


if (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then
	ds_mensagem_w := substr(ds_mensagem_w || chr(13) || chr(10) ||
			wheb_mensagem_pck.get_Texto(802163) || ds_observacao_p,1,4000);
end if;

CALL gerar_ordem_compra_hist(
	nr_ordem_compra_p,
	'S',
	ds_titulo_w,
	ds_mensagem_w,
	nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
