CREATE OR REPLACE PROCEDURE grava_hist_proc_aprov (nr_seq_aprov_compra_p bigint, nr_seq_proc_aprov_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
grava_hist_proc_aprov
Grava Historico do Processo de Aprovacao


Objetivo da procedure: Gravar cada alteracao de um processo de aprovacao com o objetivo de manter um historico de aprovacoes do usuario logado.
Parametros:
nr_seq_aprov_compra_p = Numero de sequencia da aprovacao de compra.
nm_usuario_p = Nome do usuario.
nr_seq_proc_aprov_p = Numero de sequencia do processo de aprovacao.
*/
nr_documento_w 			processo_aprov_compra_hist.nr_documento%type;		--Armazena o numero de sequencia do documento (Seq da Solicitacao de Compra  ou Seq da Cotacao de Comrpa ou Seq da Ordem de Compra ou.....).
ie_tipo_documento_w		processo_aprov_compra_hist.ie_tipo_documento%type;	--Armazena o tipo do documento ("S" de Solicitacao de Compra  ou "C" de Cotacao de Comrpa ou "O" de Ordem de Compra ou.....).
ds_tipo_documento_w		processo_aprov_compra_hist.ds_tipo_documento%type;	--Armazena a descricao do documento ("Solicitacao de Compra"  ou "Cotacao de Comrpa" ou "Ordem de Compra"ou.....).
vl_total_compra_w		processo_aprov_compra_hist.vl_total_compra%type;	--Armazena o valor total do documento da aprovacao.
ie_status_w			processo_aprov_compra_hist.ie_status%type;		--Armazena o situcao da aprovacao("P" de Pendente, "E" de encaminhado, "R" e reprovado, etc... (DOM 1014).
cd_pessoa_solicitante_w		processo_aprov_compra_hist.cd_pessoa_solicitante%type;	--Armazena o nome do solicitante(Sera vazio se o campo solicitante(nao obrigatorio) da funcao Cotacao de Compra estiver vazio).
BEGIN

	select  nr_documento,
		obter_tipo_documento_compras(nr_documento,1),
		obter_tipo_documento_compras(nr_documento,0),
		coalesce(obter_valor_processo_aprov(nr_sequencia, nr_seq_proc_aprov,obter_pessoa_fisica_usuario(nm_usuario, 'C'),0),0),
		ie_aprov_reprov,
		coalesce(substr(obter_dados_aprov_pend(nr_seq_proc_aprov,nr_documento,nm_usuario,'CD'),1,255), '')
	into STRICT	nr_documento_w,
		ds_tipo_documento_w,
		ie_tipo_documento_w,
		vl_total_compra_w,
		ie_status_w,
		cd_pessoa_solicitante_w
	from    processo_aprov_compra a
	where	a.nr_sequencia 		= nr_seq_aprov_compra_p
	and	a.nr_seq_proc_aprov 	= nr_seq_proc_aprov_p;

	insert into processo_aprov_compra_hist(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_aprov_compra,
		nr_seq_proc_aprov,
		nr_documento,
		ie_tipo_documento,
		ds_tipo_documento,
		vl_total_compra,
		ie_status,
		cd_pessoa_solicitante)
	values (nextval('processo_aprov_compra_hist_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_aprov_compra_p,
		nr_seq_proc_aprov_p,
		nr_documento_w,
		ie_tipo_documento_w,
		ds_tipo_documento_w,
		vl_total_compra_w,
		ie_status_w,
		cd_pessoa_solicitante_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
