SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE grava_info_cancel_cotacao ( nr_seq_motivo_p bigint, ds_observacao_p text, nr_cot_compra_p bigint) AS $body$
DECLARE


ds_observacao_w			varchar(4000);
ds_observacao_original_w	varchar(4000);


BEGIN

select	ds_observacao
into STRICT	ds_observacao_original_w
from	cot_compra
where	nr_cot_compra = nr_cot_compra_p;

ds_observacao_w	:=	substr(ds_observacao_p || chr(13) || chr(10) || ds_observacao_original_w,1,4000);

update	cot_compra
set	nr_seq_motivo_cancel = nr_seq_motivo_p,
	ds_observacao = ds_observacao_w
where	nr_cot_compra = nr_cot_compra_p;

/*Baixa os processos de aprovacao que ainda estejam pendentes*/

update	processo_aprov_compra
set	ie_aprov_reprov = 'B'
where	nr_sequencia in (
	SELECT	distinct(nr_seq_aprovacao)
	from	cot_compra_item
	where	nr_cot_compra = nr_cot_compra_p)
and	ie_aprov_reprov = 'P';

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
